import { CART_ADD_ITEM ,CART_REMOVE_ITEM, CART_SAVE_PAYMENT_METHOD, CART_SAVE_SHIPPING_ADDRESS} from '../Constants/cartConstant'

export const cartReducer = (state={cartItems:[]},action)=>{
    switch (action.type) {
        case CART_ADD_ITEM:
            const item = action.payload;
            // console.log(item)
            // console.log(state)
            const existingItem = state.cartItems.find(x=>x.product===item.product) 
            // console.log(existingItem)
            if(existingItem){
                return{
                    ...state,
                    cartItems:state.cartItems.map(x=>x.product===existingItem.product?{...x,qty:x.qty+item.qty}:x)
                }
            }

            else{
                return{
                    ...state,cartItems:[...state.cartItems,item]
                }

            };
            case CART_REMOVE_ITEM:return{
                ...state,
                cartItems:state.cartItems.filter(x=>x.product!==action.payload)
            }
            case CART_SAVE_SHIPPING_ADDRESS:return{
                ...state,
                shippingAddress:action.payload
            }
            case CART_SAVE_PAYMENT_METHOD:return{
                ...state,
                paymentMethod:action.payload
            }
            default:return state
        } 
}
